import React, { useRef } from 'react';

const cards = [
    {
      title: 'Card 1',
      description: 'This is the first card.',
      imageUrl: 'https://via.placeholder.com/150',
    },
    {
      title: 'Card 2',
      description: 'This is the second card.',
      imageUrl: 'https://via.placeholder.com/150',
    },
    {
      title: 'Card 3',
      description: 'This is the third card.',
      imageUrl: 'https://via.placeholder.com/150',
    },
    {
      title: 'Card 4',
      description: 'This is the fourth card.',
      imageUrl: 'https://via.placeholder.com/150',
    },
  ];



function ProductSection() {

    const carouselRef = useRef(null);

  const scrollLeft = () => {
    if (carouselRef.current) {
      carouselRef.current.scrollBy({ left: -300, behavior: 'smooth' });
    }
  };

  const scrollRight = () => {
    if (carouselRef.current) {
      carouselRef.current.scrollBy({ left: 300, behavior: 'smooth' });
    }
  };
  return (
    <>
     <div className="flex flex-col ">
        <h1>Nuestros Productos</h1>
        <p>Encontra aquello que mas te gusta</p>
     </div>

    <div className="relative w-full">
      <button
        onClick={scrollLeft}
        className="absolute left-0 top-1/2 transform -translate-y-1/2 p-2 bg-gray-800 text-white rounded-full shadow-lg z-10"
      >
        &#9664; {/* Left arrow */}
      </button>
      <div
        ref={carouselRef}
        className="w-full overflow-x-scroll whitespace-nowrap scroll-smooth scrollbar-hide"
      >
        {cards.map((card, index) => (
          <div
            key={index}
            className="inline-block w-64 mx-2 bg-white rounded-lg shadow-md"
          >
            <img
              className="w-full h-32 object-cover rounded-t-lg"
              src={card.imageUrl}
              alt={card.title}
            />
            <div className="p-4">
              <h2 className="text-xl font-bold">{card.title}</h2>
              <p className="text-gray-600">{card.description}</p>
            </div>
          </div>
        ))}
      </div>
      <button
        onClick={scrollRight}
        className="absolute right-0 top-1/2 transform -translate-y-1/2 p-2 bg-gray-800 text-white rounded-full shadow-lg z-10"
      >
        &#9654; {/* Right arrow */}
      </button>
    </div>
    
    </>
    
  );
}

export { ProductSection };
